      		var clip = null;
		(function ($) {
			$.ZeroCB = $.fn.ZeroCB = function(data) {
				// setup single ZeroClipboard object for all our elements
				ZeroClipboard.setMoviePath('generic/swf/ZeroClipboard.1.0.7.swf');
				clip = new ZeroClipboard.Client();
				clip.setHandCursor( true );

				// assign a common mouseover function for all elements using jQuery
				$('span.CopyToClipBoard').mouseover( function() {
					var link = $('span.CopyToClipBoard').attr('title');
					clip.setText( link );

					// reposition the movie over our element
					// or create it if this is the first time
					if (clip.div) {
						clip.receiveEvent('mouseout', null);
						clip.reposition(this);
					}
					else clip.glue(this);

					// gotta force these events due to the Flash movie
					// moving all around.  This insures the CSS effects
					// are properly updated.
					clip.receiveEvent('mouseover', null);
					$('.CopyToClipBoardOverlay').attr("title", "Copy the directLink to this answer to your clipboard");
				} );
			};
		})(jQuery);

		jQuery.fn.addToLastViewed = function() {

		};

		jQuery.fn.scrollToAnswer = function() {
			if($("div.result > *").length >= 1) {
				$.fancybox.close();
				$('a.fancybox').fancybox({
					'hideOnContentClick': true,
					'hideOnOverlayClick': true,
					'overlayShow': false,
					'overlayOpacity': 0.3,
					'overlayColor': '#666666',
					'titleShow': true,
					'titlePosition': 'over',
					'transitionIn': 'elastic',
					'transitionOut': 'elastic',
					'speedIn':300,
					'speedIn':300,
					'padding': 0,
					'showCloseButton':true,
					'showNavArrows':true,
			 		'enableEscapeButton':true				});
				$("html,body").delay(500).animate({scrollTop: $("div.result").offset().top}, 500);
				$("#contentMiddle").slideUp();
				$("a.registerButton").attr("href","https://t4.answers.travian.com/index.php");
				$('#rateAnswer').ready(function(){
					$('#rateSubmit', this).attr('disabled', 'disabled');
					$('input[type="radio"]').change(function(){ if($(this).val != ''){ $('#rateSubmit').removeAttr('disabled'); showRecaptcha('recaptcha_div'); } });
				});
				$('#emailAnswer').ready(function(){
					$('#emailSubmit', this).attr('disabled', 'disabled');
					$('#emailUseremail').change(function(){ if($(this).val != ''){ $('#emailSubmit').removeAttr('disabled'); showRecaptcha('recaptcha_div'); } });
				});
				$.ZeroCB();
			}
		};

